import styled from "styled-components";

export const StyledLivro = styled.div`
    display: block;
    border: 5px;
    border-color: black;
    width: 270px;
    height: 400px;
    font-size: 12px;
    background-color: gray;
    margin:10px;
    align-items: center;
`

export const StyledImage = styled.img`
    width:100%;
`
