import styled from "styled-components";

export const StyledLivro = styled.div`


    &:hover::before {
        box-shadow: 0 0 50px rgba(0, 0, 0, 0.3);
        opacity: 1;
        cursor: pointer;
  }



`

export const StyledImage = styled.img`
    width:100%;
    height:100%;


`
